mapper.select:
SELECT
-- 级联表的字段 ,yonghu.yonghu_name as yonghuName ,yonghu.yonghu_photo as yonghuPhoto ,yonghu.yonghu_phone as yonghuPhone ,yonghu.yonghu_id_number as yonghuIdNumber ,yonghu.yonghu_email as yonghuEmail ,yonghu.new_money as newMoney ,yonghu.huanyuan_types as huanyuanTypes ,yonghu.huiyuandaoqi_time as huiyuandaoqiTime ,yonghu.yonghu_delete as yonghuDelete
FROM xiaofeijilu a left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id
and a.id in
#{item}
and ( a.yonghu_id = #{params.yonghuId} )
and a.xiaofeijilu_name like CONCAT('%',#{params.xiaofeijiluName},'%')
and a.xiaofeijilu_qian >= #{params.xiaofeijiluQianStart}
and a.xiaofeijilu_qian <= #{params.xiaofeijiluQianEnd}
and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart})
and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd})
and a.yonghu_id IS NOT NULL
and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')
and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')
and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')
and yonghu.yonghu_email like CONCAT('%',#{params.yonghuEmail},'%')
and yonghu.new_money >= #{params.newMoneyStart}
and yonghu.new_money <= #{params.newMoneyEnd}
and yonghu.huanyuan_types = #{params.huanyuanTypes}
and UNIX_TIMESTAMP(yonghu.huiyuandaoqi_time) >= UNIX_TIMESTAMP(#{params.huiyuandaoqiTimeStart})
and UNIX_TIMESTAMP(yonghu.huiyuandaoqi_time) <= UNIX_TIMESTAMP(#{params.huiyuandaoqiTimeEnd})
and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}
and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}
and yonghu.yonghu_delete = #{params.yonghuDelete}
order by a.${params.orderBy} desc
